select HOUR(STR_TO_DATE(created_local, '%Y-%m-%d %H')) - 12 created_local_hour,round((sum(Accepted_To_Pickup)/count(Accepted_To_Pickup)),2) avg_accepted_To_Pickup,round((sum(available_to_accept)/count(available_to_accept)),2) avg_available_to_accept,round((sum(pickup_to_destination)/count(pickup_to_destination)),2) avg_pickup_to_destination from getswift_api_deliveries_latest  where STR_TO_DATE(created_local, '%Y-%m-%d') between '%s' and '%s' group by 1