select Driver_ID,round(sum(Distance_By_Road_Km),2) as Total_Distance,round(sum(Pickup_To_Destination),2) as Total_Time,round(sum(Total_Price),2),count(Driver_ID) as Total_Orders ,round((sum(Distance_By_Road_Km)/sum(Pickup_To_Destination))*count(Driver_ID),2) as score from cloudhiti.getswift_api_deliveries_latest where STR_TO_DATE(created_local, '%Y-%m-%d') between '%s' and '%s' group by 1 order by 6 asc limit 5
